screen.applet:
def updateAltPicon(self):
	if self.renders['Picon'].pic_filename == '':
		self.renders['AltPicon'].instance.show()
	else:
		self.renders['AltPicon'].instance.hide()
from enigma import eTimer
from Tools.BoundFunction import boundFunction
self.utimer = eTimer()
self.utimer.callback.append(boundFunction(updateAltPicon, self))
self.utimer.start(200, False)

